import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskForm from "/home/deq/Desktop/Task_1/task_management_ui/src/components/list/TaskForm.js";
import TaskList from "./components/list";
import TaskModal from "./components/TaskModal";
import './App.css'

const App = () => {
  const [tasks, setTasks] = useState();
  const [callApi, setCallApi] = useState(false)
  const [taskData, setTaskData] = useState()
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch tasks from the server on component mount
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:3000/tasks');
        setTasks(response?.data?.tasks)
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  // Function to add a new task to the list
  const handleCreateTask = (newTask) => 
    setCallApi(!callApi)

  // Function to update the status of a task
  const handleUpdateTask = (taskId) => {
    const taskToUpdate = tasks.find(task => task.id === taskId);
    setTaskData(taskToUpdate);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setTaskData(null);
  };

  // Function to delete a task
  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:3000/tasks/${taskId}`);
      setCallApi(!callApi)
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h1>Task Management</h1>
      <TaskForm onCreateTask={handleCreateTask} />
      {taskData && <TaskForm />}
      { <TaskList tasks={tasks} onUpdateTask={handleUpdateTask} onDeleteTask={handleDeleteTask} />}
      {isModalOpen && <TaskModal taskData={taskData} onUpdateTask={() => {}} onClose={handleCloseModal} />}
    </div>
  );
};

export default App;
