import TaskForm from "./components/list/create";
import TaskList from "./com"
const App = () => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the server on component mount
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:3000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  // Function to add a new task to the list
  const handleCreateTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Function to update the status of a task
  const handleUpdateTask = async (taskId) => {
    try {
      // Update task status
      await axios.put(`http://localhost:3000/tasks/${taskId}`, {
        status: 'In Progress' // Update status to 'In Progress'
      });

      // Fetch updated task list from the server
      const response = await axios.get('http://localhost:3000/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Function to delete a task
  const handleDeleteTask = async (taskId) => {
    try {
      // Delete task
      await axios.delete(`http://localhost:3000/tasks/${taskId}`);

      // Fetch updated task list from the server
      const response = await axios.get('http://localhost:3000/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h1>Task Management</h1>
      <TaskForm onCreateTask={handleCreateTask} />
      <TaskList tasks={tasks} onUpdateTask={handleUpdateTask} onDeleteTask={handleDeleteTask} />
    </div>
  );
};

export default App;
