import React from 'react';
import TaskForm from './list/TaskForm';


const TaskModal = ({ taskData, onUpdateTask, onClose }) => {
  const handleSubmit = (updatedTaskData) => {
    onUpdateTask(taskData.id, updatedTaskData);
    onClose();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <TaskForm taskData={taskData} onSubmit={handleSubmit} />
      </div>
    </div>
  );
};

export default TaskModal;
